import React, { useState, useRef, useEffect } from 'react';
import Sketch from 'react-p5';
import './style.css';
import canvasToImage from 'canvas-to-image';
import * as ApiService from '../../ApiService';

function ThreeDTree({ hide3DTree, setHide3DTree, post, setPost }) {
  const [refresh, setRefresh] = useState(false);
  // const [iteration, setIteration] = useState(6);
  // const [rightAngle, setRightAngle] = useState(30);
  // const [leftAngle, setLeftAngle] = useState(30);
  // const [ratio, setRatio] = useState(0.55);
  // const [comp, setComp] = useState(null);
  const canvasRef = useRef(null);

  const setup = (p5, canvasParentRef) => {
    p5.createCanvas(730, 520, p5.WEBGL).parent(canvasParentRef);
    canvasRef.current = p5.canvas;
    p5.angleMode(p5.DEGREES);

    p5.noLoop();

    const button = document.getElementById('refresh');
    button.addEventListener('click', () => {
      p5.redraw();
    });
  };

  const draw = (p5) => {
    p5.background(200);
    p5.stroke(0);
    p5.translate(0, 200, 0);
    p5.rotateY(p5.frameCount);

    const branch = (len) => {
      p5.strokeWeight(p5.map(len, 10, 100, 0.5, 5));
      p5.stroke(70, 40, 20);
      p5.line(0, 0, 0, 0, -len - 2, 0);
      p5.translate(0, -len, 0);
      if (len > 10) {
        for (let i = 0; i < 3; i++) {
          p5.rotateY(p5.random(100, 140));

          p5.push();
          p5.rotateZ(p5.random(20, 50));
          branch(len * 0.8);
          p5.pop();
        }
      } else {
        let r = 80 + p5.random(-20, 20);
        let g = 120 + p5.random(-20, 20);
        let b = 40 + p5.random(-20, 20);

        p5.fill(r, g, b, 200);
        p5.translate(5, 0, 0);
        p5.rotateZ(5, 0, 0);

        p5.beginShape();
        for (let i = 45; i < 135; i++) {
          let rad = 7;
          let x = rad * p5.cos(i);
          let y = rad * p5.sin(i);
          p5.vertex(x, y);
        }
        for (let i = 135; i > 45; i--) {
          let rad = 7;
          let x = rad * p5.cos(i);
          let y = rad * p5.sin(-i) + 10;
          p5.vertex(x, y);
        }
        p5.endShape(p5.CLOSE);
      }
    };

    branch(100);
  };

  const saveCanvasAsImage = async () => {
    canvasToImage(canvasRef.current);
  };

  const postCanvasAsImage = async () => {
    setHide3DTree(true);
    await ApiService.postImage(canvasRef.current);
    setPost(!post);
  };

  if (hide3DTree) {
    return null;
  }

  return (
    <div className='tree'>
      <Sketch setup={setup} draw={draw}></Sketch>

      <form>
        {/* <div className='parameter'>
          <span>Iterations:</span>
          <label>{iteration}</label>
          <input
            type='range'
            name='tree'
            id='iteration'
            min='0'
            max='12'
            step='1'
            value={iteration}
            onChange={(e) => {
              setIteration(e.target.value);
            }}
          ></input>
        </div>
        <div className='parameter'>
          <span>Right Angle:</span>
          <label>{rightAngle}°</label>
          <input
            type='range'
            name='tree'
            id='angle'
            min='0'
            max='90'
            step='1'
            value={rightAngle}
            onChange={(e) => {
              setRightAngle(e.target.value);
            }}
          ></input>
          <br />
        </div>
        <div className='parameter'>
          <span>Left Angle:</span>
          <label>{leftAngle}°</label>
          <input
            type='range'
            name='tree'
            id='angle'
            min='0'
            max='90'
            step='1'
            value={leftAngle}
            onChange={(e) => {
              setLeftAngle(e.target.value);
            }}
          ></input>
          <br />
        </div>
        <div className='parameter'>
          <span>Branch ratio:</span>
          <label>{ratio}</label>
          <input
            type='range'
            name='tree'
            id='ratio'
            min='0.5'
            max='0.79'
            step='0.01'
            value={ratio}
            onChange={(e) => {
              setRatio(e.target.value);
            }}
          ></input>
        </div> */}
      </form>
      <div className='buttons'>
        <button
          id='refresh'
          onClick={() => {
            setRefresh(!refresh);
          }}
        >
          Refresh
        </button>
        <button onClick={postCanvasAsImage}>Submit</button>
        <button onClick={saveCanvasAsImage}>Download</button>
      </div>
      <div className='close' onClick={() => setHide3DTree(true)}>
        X
      </div>
    </div>
  );
}

export default ThreeDTree;
